function SlideHowTo(e){0==e&&0==position?(position=1,howToSlider.clearQueue().animate({left:-howToSectionWidth},1e3,function(){})):1==e&&1==position&&(position=0,howToSlider.clearQueue().animate({left:"0"},1e3,function(){}))}jQuery(document).ready(function(){jQuery("#designer-how").click(function(e){e.preventDefault(),SlideHowTo(0)}),jQuery("#designer-how").mouseover(function(){SlideHowTo(0)}),jQuery("#designee-how").click(function(e){e.preventDefault(),SlideHowTo(1)}),jQuery("#designee-how").mouseover(function(){SlideHowTo(1)})});var howToSlider=jQuery(".how-slider"),howToSectionWidth=jQuery(".how-slider .how-section").width(),position=0;
jQuery(document).ready(function($){
    jQuery('#swap-roles').click(function(e){
        e.preventDefault();
        var userRole = jQuery(this).attr('data-role');
        var userId = jQuery(this).attr('data-id');
        var data =  {action: 'change_user_role', 'role': userRole, 'id': userId};
        var action_jqxhr = $.post(ajaxurl, data, function(response){
            var obj = JSON.parse(response);
            AE.pubsub.trigger('ae:notification', {
                msg: "Your role has been changed from " + obj.previousRole + " to " + obj.newRole,
                notice_type: 'success',
            });
            setTimeout(location.reload.bind(location), 1500);
        }).fail(function(){
            AE.pubsub.trigger('ae:notification', {
                msg: "Error: We could not swap your role, please try again.",
                notice_type: 'error',
            });
        });
    });
});